        address outSupplyToken;
    }

    struct VestingInfo {
        bool isVestingEnabled;
        uint16 vestingDuration;
        uint16 cliffDuration;
    }

    struct PoolInfo {
        uint256 poolOutSupplyAmount;
    }

    struct PostStreamActions {
        PoolInfo poolInfo;
        VestingInfo creatorVesting;
        VestingInfo beneficiaryVesting;
