nputs":[{"internalType":"uint16","name":"_streamId","type":"uint16"}],"name":"getStream","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"getStreams","outputs":[{"internalType":"address[]","name":"","type":"address[]"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"token","type":"address"}],"name":"isAcceptedInSupplyToken","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"_streamAddress","type":"address"}],"name":"isStream","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"params","outputs":[{"internalType":"uint256","name":"streamCreationFee","type":"uint256"},{"internalType":"address","name":"streamCreationFeeToken","type":"address"},{"internalType":"uint256","name":"exitFeePercent","type":"uint256"},{"internalType":"uint256","name":"minWaitingDuration","type":"uint256"},{"internalType":"uint256","name":"minBootstrappingDuration","type":"uint256"},{"internalType":"uint256","name":"minStreamDuration","type":"uint256"},{"internalType":"address","name":"feeCollector","type":"address"},{"internalType":"address","name":"protocolAdmin","type":"address"},{"internalType":"string","name":"tosVersion","type":"string"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"creator","type":"address"},{"internalType":"bytes32","name":"_salt","type":"bytes32"},{"internalType":"bytes32","name":"bytecodeHash","type":"bytes32"}],"name":"predictAddress","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"pure","type":"function"},{"inputs":[{"internalType":"bool","name":"_frozen","type":"bool"}],"name":"setFrozen","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint16","name":"","