pragma solidity >=0.8.0 <0.9.0;

interface IStreamErrors {
    // Stream errors
    error InvalidBootstrappingStartTime();
    error InvalidStreamStartTime();
    error InvalidStreamEndTime();
    error StreamDurationTooShort();
    error BootstrappingDurationTooShort();
    error WaitingDurationTooShort();
    error InsufficientTokenPayment(uint256 requiredTokenAmount, uint256 tokenBalance);
    error InvalidOutSupplyToken();
    error InvalidInSupplyToken();
    error PaymentFailed();
    error OperationNotAllowed();
    error Unauthorized();
    error InvalidWithdrawAmount();
    error WithdrawAmountExceedsBalance(uint256 cap);
    error InsufficientOutAmount();
    // StreamFactory errors
    error ContractFrozen();
    error ZeroOutSupplyNotAllowed();
    error StreamInputTokenNotAccepted();
    error InvalidBootstrappingTime();
    error StreamStartMustBeAfterBootstrapping();
    error StreamEndMustBeAfterStart();
    error InvalidToSVersion();
    error InsufficientNativeToken();
    error FeeTransferFailed();
    error TokenTransferFailed();
    error StreamAddressPredictionFailed();
    error InvalidFeeCollector();
    error InvalidProtocolAdmin();
    error NotAdmin();
} ","keccak256":"0x35981c293ad8143f03db46b4af890a12c20cb48a78fbb326551121f273c6a9d8","license":"MIT"},"contracts/StreamEvents.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./StreamTypes.sol";

interface IStreamEvents {
    event StreamCreated(
        address indexed streamOutToken,
        address indexed streamInToken,
        address indexed streamFactoryAddress,
        uint256 streamOutAmount,
        uint256 bootstrappingStartTime,
        uint256 streamStartTime,
        uint256 streamEndTime,
        uint256 threshold,
        string streamName,
        string tosVersion,
        address streamAddress,
        uint16 streamId
    );

    event StreamSynced(IStreamTypes.Status mainStatus, uint256 lastUpdated);

    event Subscribed(address indexed subscriber, uint256 amountIn, uint256 newShares);
    event Withdrawn(address indexed subscriber, uint256 amountIn);
    event Exited(address indexed subscriber, uint256 purchased);
    event StreamFinalized(address indexed creator, uint256 spentIn, uint256 outRemaining, IStreamTypes.Status status);
    event ParamsUpdated();
    event FeeCollectorUpdated(address newFeeCollector);
    event ProtocolAdminUpdated(address newProtocolAdmin);
    event FrozenStateUpdated(bool frozen);
}
","keccak256":"0xba6c26dcb6f3491d054f36267adec361c946853a106ae5c6300a3f5ad1c7aa9f","license":"MIT"},"contracts/StreamFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./Stream.sol";
import "./StreamEvents.sol";
import "./StreamErrors.sol";

contract StreamFactory is IStreamEvents, IStreamErrors {
    struct Params {
        uint256 streamCreationFee; // Fixed fee to create a stream
        address streamCreationFeeToken; // Token used for creation fee,
        uint256 exitFeePercent; // Fee percentage when exiting a stream
        uint256 minWaitingDuration; // Minimum waiting period
        uint256 minBootstrappingDuration; // Minimum bootstrapping period
        uint256 minStreamDuration; // Minimum duration for a stream
        address feeCollector; // Address where fees are collected
        address protocolAdmin; // Admin address for protocol
        string tosVersion; // Terms of service version
    }

    mapping(address => bool) public acceptedInSupplyTokens;

    address public constant NATIVE_TOKEN = address(0);

    uint16 public currentStreamId;

    Params public params;
    mapping(uint16 => address) public streamAddresses;

    bool public frozen;

    constructor(
        uint256 _streamCreationFee,
        address _streamCreationFeeToken,
        uint256 _exitFeePercent,
        uint256 _minWaitingDuration,
        uint256 _minBootstrappingDuration,
        uint256 _minStreamDuration,
        address[] memory _acceptedInSupplyTokens,
        address _feeCollector,
        address _protocolAdmin,
        string memory _tosVersion
    ) {
        if (_feeCollector == address(0)) revert InvalidFeeCollector();
        if (_protocolAdmin == address(0)) revert InvalidProtocolAdmin();

        params = Params({
            streamCreationFee: _streamCreationFee,
            streamCreationFeeToken: _streamCreationFeeToken,
            exitFeePercent: _exitFeePercent,
            minWaitingDuration: _minWaitingDuration,
            minBootstrappingDuration: _minBootstrappingDuration,
            minStreamDuration: _minStreamDuration,
            feeCollector: _feeCollector,
            protocolAdmin: _protocolAdmin,
            tosVersion: _tosVersion
        });

        // Set accepted tokens
        for (uint i = 0; i < _acceptedInSupplyTokens.length; i++) {
            acceptedInSupplyTokens[_acceptedInSupplyTokens[i]] = true;
        }
        currentStreamId = 0;
    }

    modifier onlyAdmin() {
        if (msg.sender != params.protocolAdmin) revert NotAdmin();
        _;
    }

    function updateParams(
        uint256 _streamCreationFee,
        uint256 _exitFeePercent,
        uint256 _minWaitingDuration,
        uint256 _minBootstrappingDuration,
        uint256 _minStreamDuration,
        string memory _tosVersion
    ) external onlyAdmin {
        params.streamCreationFee = _streamCreationFee;
        params.exitFeePercent = _exitFeePercent;
        params.minWaitingDuration = _minWaitingDuration;
        params.minBootstrappingDuration = _minBootstrappingDuration;
        params.minStreamDuration = _minStreamDuration;
        params.tosVersion = _tosVersion;

        emit ParamsUpdated();
    }

    function updateFeeCollector(address _feeCollector) external onlyAdmin {
        if (_feeCollector == address(0)) revert InvalidFeeCollector();
        params.feeCollector = _feeCollector;
        emit FeeCollectorUpdated(_feeCollector);
    }

    function updateProtocolAdmin(address _protocolAdmin) external onlyAdmin {
        if (_protocolAdmin == address(0)) revert InvalidProtocolAdmin();
        params.protocolAdmin = _protocolAdmin;
        emit ProtocolAdminUpdated(_protocolAdmin);
    }

    function updateAcceptedTokens(
        address[] calldata tokens_to_add,
        address[] calldata tokens_to_remove
    ) external onlyAdmin {
        for (uint i = 0; i < tokens_to_add.length; i++) {
            acceptedInSupplyTokens[tokens_to_add[i]] = true;
        }
        for (uint i = 0; i < tokens_to_remove.length; i++) {
            acceptedInSupplyTokens[tokens_to_remove[i]] = false;
        }
    }

    function isAcceptedInSupplyToken(address token) public view returns (bool) {
        return acceptedInSupplyTokens[token];
    }

    function createStream(
        uint256 _streamOutAmount,
        address _outSupplyToken,
        uint256 _bootstrappingStartTime,
        uint256 _streamStartTime,
        uint256 _streamEndTime,
        uint256 _threshold,
        string memory _name,
        address _inSupplyToken,
        string memory _tosVersion,
        bytes32 _salt
    ) external payable {
        // Check if contract is accepting new streams (not frozen)
        if (frozen) revert ContractFrozen();

        // Validate input parameters
        if (_streamOutAmount == 0) revert ZeroOutSupplyNotAllowed();
        if (!acceptedInSupplyTokens[_inSupplyToken]) revert StreamInputTokenNotAccepted();

        // Validate time parameters using validateStreamTimes
        validateStreamTimes(block.timestamp, _bootstrappingStartTime, _streamStartTime, _streamEndTime);

        // Validate TOS version
        if (keccak256(abi.encodePacked(_tosVersion)) != keccak256(abi.encodePacked(params.tosVersion)))
            revert InvalidToSVersion();

        // Load creation fee
        uint256 creationFee = params.streamCreationFee;
        if (creationFee > 0) {
            if (params.streamCreationFeeToken == address(0)) {
                // Native token
                if (msg.value < creationFee) revert InsufficientNativeToken();
                // Transfer fee to fee collector
                if (!payable(params.feeCollector).send(creationFee)) revert FeeTransferFailed();
            } else {
                // ERC20 token
                if (
                    !IERC20(params.streamCreationFeeToken).transferFrom(
                        msg.sender,
                        address(params.feeCollector),
                        creationFee
                    )
                ) revert TokenTransferFailed();
            }
        }
        // Predict stream address
        bytes32 bytecodeHash = keccak256(
            abi.encodePacked(
                type(Stream).creationCode,
                abi.encode(
                    _streamOutAmount,
                    _outSupplyToken,
                    _bootstrappingStartTime,
                    _streamStartTime,
                    _streamEndTime,
                    _threshold,
                    _name,
                    _inSupplyToken,
                    msg.sender
                )
            )
        );

        address predictedAddress = predictAddress(address(this), _salt, bytecodeHash);
        // Transfer out denom to stream contract
        if (!IERC20(_outSupplyToken).transferFrom(msg.sender, predictedAddress, _streamOutAmount))
            revert TokenTransferFailed();
        // Deploy new stream contract with all parameters
        Stream newStream = new Stream{ salt: _salt }(
            _streamOutAmount,
            _outSupplyToken,
            _bootstrappingStartTime,
            _streamStartTime,
            _streamEndTime,
            _threshold,
            _name,
            _inSupplyToken,
            msg.sender
        );

        if (address(newStream) != predictedAddress) revert StreamAddressPredictionFailed();
        streamAddresses[currentStreamId] = address(newStream);

        emit StreamCreated(
            _outSupplyToken,
            _inSupplyToken,
            address(this),
            _streamOutAmount,
            _bootstrappingStartTime,
            _streamStartTime,
            _streamEndTime,
            _threshold,
            _name,
            _tosVersion,
            address(newStream),
            currentStreamId
        );
        currentStreamId++;
    }

    function getStreams() external view returns (address[] memory) {
        address[] memory streams = new address[](currentStreamId);
        for (uint16 i = 0; i < currentStreamId; i++) {
            streams[i] = streamAddresses[i];
        }
        return streams;
    }

    function getStream(uint16 _streamId) external view returns (address) {
        return streamAddresses[_streamId];
    }

    function isStream(address _streamAddress) external view returns (bool) {
        for (uint16 i = 0; i < currentStreamId; i++) {
            if (streamAddresses[i] == _streamAddress) {
                return true;
            }
        }
        return false;
    }

    function getParams() external view returns (Params memory) {
        return params;
    }

    // Optional: Add ability to transfer ownership
    function transferOwnership(address newOwner) external onlyAdmin {
        if (newOwner == address(0)) revert InvalidProtocolAdmin();
        params.protocolAdmin = newOwner;
    }

    function setFrozen(bool _frozen) external onlyAdmin {
        frozen = _frozen;
        emit FrozenStateUpdated(_frozen);
    }

    function predictAddress(address creator, bytes32 _salt, bytes32 bytecodeHash) public pure returns (address) {
        return address(uint160(uint(keccak256(abi.encodePacked(bytes1(0xff), creator, _salt, bytecodeHash)))));
    }

    function validateStreamTimes(
        uint256 nowTime,
        uint256 _bootstrappingStartTime,
        uint256 _startTime,
        uint256 _endTime
    ) internal view {
        if (nowTime > _bootstrappingStartTime) revert InvalidBootstrappingStartTime();
        if (_bootstrappingStartTime > _startTime) revert InvalidStreamStartTime();
        if (_startTime > _endTime) revert InvalidStreamEndTime();
        if (_endTime - _startTime < params.minStreamDuration) revert StreamDurationTooShort();
        if (_startTime - _bootstrappingStartTime < params.minBootstrappingDuration)
            revert BootstrappingDurationTooShort();
        if (_bootstrappingStartTime - nowTime < params.minWaitingDuration) revert WaitingDurationTooShort();
    }
}
","keccak256":"0x56efe4a5f6ad863d5ab8ae0dee787b3fdc0ddcd80c2926f046e2989d896d7c17","license":"MIT"},"contracts/StreamMathLib.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./StreamTypes.sol";
import "./DecimalMath.sol";
import "./PositionTypes.sol";

library StreamMathLib {
    /**
     * @dev Calculates the time difference ratio between current time and last updated time
     * @param currentTimestamp Current timestamp
     * @param streamStartTime Timestamp when the stream starts
     * @param streamEndTime Timestamp when the stream ends
     * @param lastUpdated Timestamp when the stream was last updated
     * @return The ratio of time elapsed since last update compared to total remaining time (scaled by 1e18)
     */
    function calculateDiff(
        uint256 currentTimestamp,
        uint256 streamStartTime,
        uint256 streamEndTime,
        uint256 lastUpdated
    ) internal pure returns (uint256) {
        // If the stream is not started yet or already ended, return 0
        if (currentTimestamp < streamStartTime || lastUpdated >= streamEndTime) {
            return 0;
        }

        // If lastUpdated is before start time, set it to start time
        uint256 effectiveLastUpdated = lastUpdated;
        if (effectiveLastUpdated < streamStartTime) {
            effectiveLastUpdated = streamStartTime;
        }

        // If current time is past end time, use end time instead
        uint256 effectiveNow = currentTimestamp;
        if (effectiveNow > streamEndTime) {
            effectiveNow = streamEndTime;
        }

        uint256 numerator = effectiveNow - effectiveLastUpdated;
        uint256 denominator = streamEndTime - effectiveLastUpdated;

        if (denominator == 0 || numerator == 0) {
            return 0;
        }
        // Return ratio of time elapsed since last update compared to total remaining time
        return (numerator * 1e18) / denominator;
    }

    /**
     * @dev Calculates the stream status based on the current state and timestamp
     * @param currentStatus Current status of the stream
     * @param currentTime Current timestamp to check against
     * @param bootstrappingStartTime Timestamp when bootstrapping phase starts
     * @param streamStartTime Timestamp when active streaming starts
     * @param streamEndTime Timestamp when streaming ends
     * @return IStreamTypes.Status The calculated stream status
     */
    function calculateStreamStatus(
        IStreamTypes.Status currentStatus,
        uint256 currentTime,
        uint256 bootstrappingStartTime,
        uint256 streamStartTime,
        uint256 streamEndTime
    ) internal pure returns (IStreamTypes.Status) {
        // Don't update if stream is in a final state
        if (
            currentStatus == IStreamTypes.Status.Cancelled ||
            currentStatus == IStreamTypes.Status.FinalizedRefunded ||
            currentStatus == IStreamTypes.Status.FinalizedStreamed
        ) {
            return currentStatus;
        }

        // Update status based on current timestamp
        if (currentTime < bootstrappingStartTime) {
            return IStreamTypes.Status.Waiting;
        } else if (currentTime >= bootstrappingStartTime && currentTime < streamStartTime) {
            return IStreamTypes.Status.Bootstrapping;
        } else if (currentTime >= streamStartTime && currentTime < streamEndTime) {
            return IStreamTypes.Status.Active;
        } else if (currentTime >= streamEndTime) {
            return IStreamTypes.Status.Ended;
        }

        // This should never be reached, but return current status as fallback
        return currentStatus;
    }

    /**
     * @dev Calculates updated stream state based on time difference
     * @param state Current stream state
     * @param diff Time difference in seconds
     * @return Updated stream state
     */
    function calculateUpdatedState(
        IStreamTypes.StreamState memory state,
        uint256 diff
    ) internal pure returns (IStreamTypes.StreamState memory) {
        // Create a copy of the state to avoid modifying the input
        IStreamTypes.StreamState memory newState = state;

        if (newState.shares > 0 && diff > 0) {
            // Calculate new distribution balance and spent in amount
            uint256 newDistributionBalance = (newState.outRemaining * diff) / 1e18;
            uint256 spentIn = (newState.inSupply * diff) / 1e18;

            // Update state variables
            newState.spentIn += spentIn;
            newState.inSupply -= spentIn;

            if (newDistributionBalance > 0) {
                newState.outRemaining -= newDistributionBalance;
                // Update distribution index (shares are in base units, multiply by 1e18 for precision)
                newState.distIndex += (newDistributionBalance * 1e18) / newState.shares;
                // Update current streamed price
                newState.currentStreamedPrice = (spentIn * 1e18) / newDistributionBalance;
            }
        }

        return newState;
    }

    function computeSharesAmount(
        uint256 amountIn,
        bool roundUp,
        uint256 inSupply,
        uint256 totalShares
    ) internal pure returns (uint256) {
        if (totalShares == 0 || amountIn == 0) {
            return amountIn;
        }

        uint256 totalSharesIn = totalShares * amountIn;
        if (roundUp) {
            return (totalSharesIn + inSupply - 1) / inSupply;
        } else {
            return totalSharesIn / inSupply;
        }
    }

    /**
     * @dev Calculates the exit fee amount based on the spent in amount
     * @param spentInAmount Amount of tokens spent in the stream
     * @return feeAmount The calculated fee amount
     * @return remainingAmount The remaining amount after fee deduction
     */
    function calculateExitFee(
        uint256 spentInAmount,
        uint256 exitFeePercent
    ) internal pure returns (uint256 feeAmount, uint256 remainingAmount) {
        uint256 decimalExitFee = exitFeePercent;
        uint256 decimalSpentIn = DecimalMath.fromNumber(spentInAmount);

        // Calculate exit fee amount using DecimalMath
        uint256 exitFeeAmount = DecimalMath.mul(decimalSpentIn, decimalExitFee);
        feeAmount = DecimalMath.floor(exitFeeAmount);
        remainingAmount = spentInAmount - feeAmount;

        return (feeAmount, remainingAmount);
    }

    function syncPosition(
        PositionTypes.Position memory position,
        uint256 distIndex,
        uint256 shares,
        uint256 inSupply,
        uint256 nowTime
    ) internal pure returns (PositionTypes.Position memory) {
        // Create a new position in memory to store the updated values
        PositionTypes.Position memory updatedPosition = PositionTypes.Position({
            inBalance: position.inBalance,
            shares: position.shares,
            index: position.index,
            lastUpdateTime: position.lastUpdateTime,
            pendingReward: position.pendingReward,
            spentIn: position.spentIn,
            purchased: position.purchased,
            exitDate: position.exitDate
        });

        // Calculate index difference for distributions since last update
        uint256 indexDiff = distIndex - updatedPosition.index;
        uint256 spent = 0;
        uint256 purchased = 0;

        // Only process if there are shares in the stream
        if (shares > 0) {
            // Calculate purchased amount based on position shares and index difference
            uint256 positionPurchased = (updatedPosition.shares * indexDiff) / 1e18 + updatedPosition.pendingReward;
            // Calculate remaining balance based on current shares ratio
            uint256 inRemaining = (inSupply * updatedPosition.shares) / shares;
            // Calculate spent amount
            spent = updatedPosition.inBalance - inRemaining;
            updatedPosition.spentIn += spent;
            updatedPosition.inBalance = inRemaining;

            // Update purchased amount
            purchased = positionPurchased;
            updatedPosition.purchased += purchased;
        }

        // Update position tracking
        updatedPosition.index = distIndex;
        updatedPosition.lastUpdateTime = nowTime;

        return updatedPosition;
    }
}
","keccak256":"0x6caf65976c6e92123920dd8a218b1a6337a61b82b23d4af0b461186e9fb3d546","license":"MIT"},"contracts/StreamTypes.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

interface IStreamTypes {
    enum Status {
        Waiting,
        Bootstrapping,
        Active,
        Ended,
        FinalizedRefunded,
        FinalizedStreamed,
        Cancelled
    }

    struct StatusInfo {
        Status status;
    }

    struct StreamTimes {
        uint256 bootstrappingStartTime;
        uint256 streamStartTime;
        uint256 streamEndTime;
    }

    struct StreamMetadata {
        string name;
    }

    struct StreamState {
        uint256 outRemaining;
        uint256 distIndex;
        uint256 spentIn;
        uint256 shares;
        uint256 currentStreamedPrice;
        uint256 threshold;
        uint256 inSupply;
        uint256 outSupply;
        uint256 lastUpdated;
    }

    struct StreamTokens {
        address inSupplyToken;
        address outSupplyToken;
    }
}
","keccak256":"0xf818d196fbfb1b7ed4c8ffac72fc0c1d95f7ac5f7a4d92e071a255c1699fc241","license":"MIT"},"hardhat/console.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

library console {
    address constant CONSOLE_ADDRESS =
        0x000000000000000000636F6e736F6c652e6c6f67;

    function _sendLogPayloadImplementation(bytes memory payload) internal view {
        address consoleAddress = CONSOLE_ADDRESS;
        /// @solidity memory-safe-assembly
        assembly {
            pop(
                staticcall(
                    gas(),
                    consoleAddress,
                    add(payload, 32),
                    mload(payload),
                    0,
                    0
                )
            )
        }
    }

    function _castToPure(
      function(bytes memory) internal view fnIn
    ) internal pure returns (function(bytes memory) pure fnOut) {
        assembly {
            fnOut := fnIn
        }
    }

    function _sendLogPayload(bytes memory payload) internal pure {
        _castToPure(_sendLogPayloadImplementation)(payload);
    }

    function log() internal pure {
        _sendLogPayload(abi.encodeWithSignature("log()"));
    }

    function logInt(int256 p0) internal pure {
        _sendLogPayload(abi.encodeWithSignature("log(int256)", p0));
