}
","keccak256":"0x6991ac31893c9a06dd304a8e5ecd5e123621c8b2deb69d4b26c466489fcfbb82","license":"MIT"},"contracts/Stream.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./PositionStorage.sol";
import "./PositionTypes.sol";
import "./StreamEvents.sol";
import "./StreamErrors.sol";
import "./StreamTypes.sol";
import "./StreamFactory.sol";
import "./DecimalMath.sol";
import "./StreamMathLib.sol";

import "hardhat/console.sol";

interface IERC20 {
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);

    function balanceOf(address account) external view returns (uint256);

    function transfer(address to, uint256 amount) external returns (bool);
}

contract Stream is IStreamErrors, IStreamEvents {
    address public creator;
    address public positionStorageAddress;
    string public name;

    IStreamTypes.StreamState public streamState;
    IStreamTypes.StreamTokens public streamTokens;
    IStreamTypes.StreamMetadata public streamMetadata;
    IStreamTypes.Status public streamStatus;
    IStreamTypes.StreamTimes public streamTimes;
    address public factory;

    PositionStorage public positionStorage;

    // constructor should return its address
    constructor(
        uint256 _streamOutAmount,
        address _outSupplyToken,
        uint256 _bootstrappingStartTime,
        uint256 _streamStartTime,
        uint256 _streamEndTime,
        uint256 _threshold,
        string memory _name,
        address _inSupplyToken,
        address _creator
    ) {
        // Validate that output token is a valid ERC20
        if (!isValidERC20(_outSupplyToken, msg.sender)) {
            revert InvalidOutSupplyToken();
        }

        // Check if the contract has enough balance of output token
        if (!hasEnoughBalance(_outSupplyToken, address(this), _streamOutAmount)) {
            revert InsufficientOutAmount();
        }

        // Validate that in token is a valid ERC20
        if (!isValidERC20(_inSupplyToken, msg.sender)) {
            revert InvalidInSupplyToken();
        }

        creator = _creator;
        positionStorage = new PositionStorage();
        positionStorageAddress = address(positionStorage);

        streamState = IStreamTypes.StreamState({
            distIndex: DecimalMath.fromNumber(0),
            outRemaining: _streamOutAmount,
            inSupply: 0,
            spentIn: 0,
            shares: 0,
            currentStreamedPrice: DecimalMath.fromNumber(0),
            threshold: _threshold,
            outSupply: _streamOutAmount,
            lastUpdated: block.timestamp
        });

        streamTokens = IStreamTypes.StreamTokens({ inSupplyToken: _inSupplyToken, outSupplyToken: _outSupplyToken });

        streamMetadata = IStreamTypes.StreamMetadata({ name: _name });

        streamStatus = IStreamTypes.Status.Waiting;

        streamTimes = IStreamTypes.StreamTimes({
            bootstrappingStartTime: _bootstrappingStartTime,
            streamStartTime: _streamStartTime,
            streamEndTime: _streamEndTime
        });

        // Store the factory address
        factory = msg.sender;
    }

    function syncStream(
        IStreamTypes.StreamState memory state,
        IStreamTypes.StreamTimes memory times,
        uint256 nowTime
    ) internal pure returns (IStreamTypes.StreamState memory) {
        Decimal memory diff = StreamMathLib.calculateDiff(
            nowTime,
            times.streamStartTime,
            times.streamEndTime,
            state.lastUpdated
        );

        if (diff.value > 0) {
            IStreamTypes.StreamState memory updatedState = StreamMathLib.calculateUpdatedState(state, diff);
            return updatedState;
        }

        return state;
    }

    function saveStreamState(IStreamTypes.StreamState memory state) internal {
        streamState = state;
    }

    function loadStreamState() internal view returns (IStreamTypes.StreamState memory) {
        return streamState;
    }

    /**
     * @dev Validates if an operation is allowed based on the current stream status
     * @param allowedStatuses Array of allowed statuses for the operation
     * @return bool True if the operation is allowed, false otherwise
     */
    function isOperationAllowed(
        IStreamTypes.Status currentStatus,
        IStreamTypes.Status[] memory allowedStatuses
    ) internal pure returns (bool) {
        for (uint256 i = 0; i < allowedStatuses.length; i++) {
            if (currentStatus == allowedStatuses[i]) {
                return true;
            }
        }
        return false;
    }

    /**
     * @dev Safely transfers tokens from the contract to a recipient
     * @param tokenAddress Address of the token to transfer
     * @param recipient Address of the recipient
     * @param amount Amount of tokens to transfer
     * @return bool True if the transfer was successful
     */
    function safeTokenTransfer(address tokenAddress, address recipient, uint256 amount) internal returns (bool) {
        if (amount == 0 || recipient == address(0)) {
            return true;
        }

        IERC20 token = IERC20(tokenAddress);
        bool success = token.transfer(recipient, amount);
        if (!success) {
            revert PaymentFailed();
        }
        return true;
    }

    /**
     * @dev Checks if the threshold has been reached for stream finalization
     * @return bool True if the threshold has been reached, false otherwise
     */
    function isThresholdReached(IStreamTypes.StreamState memory state) internal pure returns (bool) {
        return state.spentIn >= state.threshold;
    }

    /**
     * @dev Validates a position exists and is active
     * @param position The position to validate
     * @return bool True if the position is valid and active
     */
    function isValidActivePosition(PositionTypes.Position memory position) internal pure returns (bool) {
        return position.shares > 0 && position.exitDate == 0;
    }

    function withdraw(uint256 cap) external {
        if (cap == 0) {
            revert InvalidWithdrawAmount();
        }

        // Load position once
        PositionTypes.Position memory position = loadPosition(msg.sender);

        // Check if position is valid and active
        if (!isValidActivePosition(position)) {
            revert InvalidPosition();
        }

        if (cap > position.inBalance) {
            revert WithdrawAmountExceedsBalance(cap);
        }

        // load stream times
        IStreamTypes.StreamTimes memory times = loadStreamTimes();

        // Load and update status
        IStreamTypes.Status status = loadStreamStatus();
        status = syncStreamStatus(status, times, block.timestamp);

        // Check if operation is allowed
        IStreamTypes.Status[] memory allowedStatuses = new IStreamTypes.Status[](2);
        allowedStatuses[0] = IStreamTypes.Status.Active;
        allowedStatuses[1] = IStreamTypes.Status.Bootstrapping;
        isOperationAllowed(status, allowedStatuses);

        // Save the updated status
        saveStreamStatus(status);

        // Load and update stream state
        IStreamTypes.StreamState memory state = loadStream();
        state = syncStream(state, times, block.timestamp);

        // Sync position with the updated state
        position = StreamMathLib.syncPosition(position, state.distIndex, state.shares, state.inSupply, block.timestamp);

        if (cap == position.inBalance) {
            position.shares = 0;
            position.inBalance = 0;
        } else {
            position.shares =
                position.shares -
                StreamMathLib.computeSharesAmount(cap, true, state.inSupply, position.shares);
            position.inBalance = position.inBalance - cap;
        }

        // Update stream state
        state.inSupply = state.inSupply - cap;
        state.shares = state.shares - StreamMathLib.computeSharesAmount(cap, true, state.inSupply, state.shares);

        // Save everything at the end
        savePosition(msg.sender, position);
        saveStream(state);

        // Token transfer
        safeTokenTransfer(streamTokens.inSupplyToken, msg.sender, cap);
        emit Withdrawn(address(this), msg.sender, position.inBalance, position.shares, state.inSupply, state.shares);
    }

    function subscribe(uint256 amountIn) external payable {
        // Load status once
        IStreamTypes.Status status = loadStreamStatus();
        IStreamTypes.StreamTimes memory times = loadStreamTimes();
        // Update the loaded status
        status = syncStreamStatus(status, times, block.timestamp);

        // Check if operation is allowed with the updated status
        IStreamTypes.Status[] memory allowedStatuses = new IStreamTypes.Status[](2);
        allowedStatuses[0] = IStreamTypes.Status.Bootstrapping;
        allowedStatuses[1] = IStreamTypes.Status.Active;
        isOperationAllowed(status, allowedStatuses);
        // Save the updated status
        saveStreamStatus(status);

        // Validate if sender has enough tokens
        IERC20 streamInToken = IERC20(streamTokens.inSupplyToken);
        uint256 streamInTokenBalance = streamInToken.balanceOf(msg.sender);
        if (streamInTokenBalance < amountIn) {
            revert InsufficientTokenPayment(amountIn, streamInTokenBalance);
        }

        // Transfer tokens from sender to this contract
        bool success = streamInToken.transferFrom(msg.sender, address(this), amountIn);
        if (!success) {
            revert PaymentFailed();
        }

        // Load position once
        PositionTypes.Position memory position = loadPosition(msg.sender);

        // Load stream state once
        IStreamTypes.StreamState memory state = loadStream();

        // Update the stream state
        state = syncStream(state);

        uint256 newShares = 0;

        if (position.shares == 0) {
            // New position case
            newShares = StreamMathLib.computeSharesAmount(amountIn, false, state.inSupply, state.shares);
            position = PositionTypes.Position({
                inBalance: amountIn,
                shares: newShares,
                index: state.distIndex,
                lastUpdateTime: block.timestamp,
                pendingReward: DecimalMath.fromNumber(0),
                spentIn: 0,
                purchased: 0,
                exitDate: 0
            });
        } else {
            // Update existing position
            newShares = StreamMathLib.computeSharesAmount(amountIn, false, state.inSupply, state.shares);
            position = StreamMathLib.syncPosition(
                position,
                state.distIndex,
                state.shares,
                state.inSupply,
                block.timestamp
            );
            position.inBalance += amountIn;
            position.shares += newShares;
        }

        // Update StreamState
        state.inSupply += amountIn;
        state.shares += newShares;

        // Save everything once we're done modifying
        savePosition(msg.sender, position);
        saveStream(state);

        // Emit event
        emit Subscribed(address(this), msg.sender, amountIn, newShares, state.inSupply, state.shares);
    }

    function exitStream() external {
        // Load position
        PositionTypes.Position memory position = loadPosition(msg.sender);

        // Check if position is valid and active
        if (!isValidActivePosition(position)) {
            revert InvalidPosition();
        }

        // Load and update stream state
        IStreamTypes.StreamState memory state = loadStream();
        state = syncStream(state);

        // Sync position with updated state
        position = StreamMathLib.syncPosition(position, state.distIndex, state.shares, state.inSupply, block.timestamp);

        // Load and update status
        IStreamTypes.Status status = loadStreamStatus();
        IStreamTypes.StreamTimes memory times = loadStreamTimes();
        status = syncStreamStatus(status, times, block.timestamp);

        bool thresholdReached = isThresholdReached(state);

        // Handle token distributions based on exit scenario
        handleExitDistribution(status, thresholdReached, position);

        // Set exit date
        position.exitDate = block.timestamp;

        // Save everything
        saveStreamStatus(status);
        saveStream(state);
        savePosition(msg.sender, position);

        emit Exited(address(this), msg.sender, position.purchased, position.spentIn, block.timestamp);
    }

    function handleExitDistribution(
        IStreamTypes.Status status,
        bool thresholdReached,
        PositionTypes.Position memory position
    ) internal {
        // Case 1: Successful stream completion
        if (isSuccessfulExit(status, thresholdReached)) {
            // Return any unused input tokens
            if (position.inBalance > 0) {
                safeTokenTransfer(streamTokens.inSupplyToken, msg.sender, position.inBalance);
            }
            // Distribute earned output tokens
            safeTokenTransfer(streamTokens.outSupplyToken, msg.sender, position.purchased);
            return;
        }

        // Case 2: Refund scenario
        if (isRefundExit(status, thresholdReached)) {
            // Full refund of all input tokens (both spent and unspent)
            uint256 totalRefund = position.inBalance + position.spentIn;
            safeTokenTransfer(streamTokens.inSupplyToken, msg.sender, totalRefund);
            return;
        }

        // If neither condition is met, the exit is not allowed
        revert InvalidExitCondition();
    }

    function isSuccessfulExit(IStreamTypes.Status status, bool thresholdReached) internal pure returns (bool) {
        return
            (status == IStreamTypes.Status.Ended && thresholdReached) ||
            (status == IStreamTypes.Status.FinalizedStreamed);
    }

    function isRefundExit(IStreamTypes.Status status, bool thresholdReached) internal pure returns (bool) {
        return
            status == IStreamTypes.Status.Cancelled ||
            status == IStreamTypes.Status.FinalizedRefunded ||
            (status == IStreamTypes.Status.Ended && !thresholdReached);
    }

    function finalizeStream() external {
        assertIsCreator();

        // Load and update status
        IStreamTypes.Status status = loadStreamStatus();
        IStreamTypes.StreamTimes memory times = loadStreamTimes();
        status = syncStreamStatus(status, times, block.timestamp);

        // Check if operation is allowed
        IStreamTypes.Status[] memory allowedStatuses = new IStreamTypes.Status[](1);
        allowedStatuses[0] = IStreamTypes.Status.Ended;
        isOperationAllowed(status, allowedStatuses);

        // Load and update stream state
        IStreamTypes.StreamState memory state = loadStream();
        state = syncStream(state, times, block.timestamp);

        bool thresholdReached = isThresholdReached(state);

        if (thresholdReached) {
            // Get fee collector from factory
            StreamFactory factoryContract = StreamFactory(factory);
            StreamFactory.Params memory params = factoryContract.getParams();
            address feeCollector = params.feeCollector;
            Decimal memory exitFeeRatio = params.exitFeeRatio;

            // Calculate exit fee
            (uint256 feeAmount, uint256 creatorRevenue) = StreamMathLib.calculateExitFee(state.spentIn, exitFeeRatio);

            // Transfer fee to fee collector if needed
            if (feeAmount > 0) {
                safeTokenTransfer(streamTokens.inSupplyToken, feeCollector, feeAmount);
            }

            // Send revenue to creator
            safeTokenTransfer(streamTokens.inSupplyToken, creator, creatorRevenue);

            // Update status
            status = IStreamTypes.Status.FinalizedStreamed;

            // Refund out tokens to creator if left any
            if (state.outRemaining > 0) {
                safeTokenTransfer(streamTokens.outSupplyToken, creator, state.outRemaining);
            }
        } else {
            // Update status
            status = IStreamTypes.Status.FinalizedRefunded;

            // Refund out tokens to creator
            safeTokenTransfer(streamTokens.outSupplyToken, creator, state.outSupply);
        }

        // Save everything
        saveStreamStatus(status);
        saveStream(state);

        emit StreamFinalized(address(this), creator, state.spentIn, state.outRemaining, status);
    }

    function syncStreamExternal() external {
        // Load, update and save stream state
        IStreamTypes.StreamState memory state = loadStream();
        IStreamTypes.StreamTimes memory times = loadStreamTimes();
        state = syncStream(state, times, block.timestamp);
        saveStream(state);

        // Load, update and save status
        IStreamTypes.Status status = loadStreamStatus();
        status = syncStreamStatus(status, times, block.timestamp);
        saveStreamStatus(status);

        emit StreamSynced(
            address(this),
            state.lastUpdated,
            uint8(status),
            state.distIndex,
            state.outRemaining,
            state.inSupply,
            state.spentIn,
            state.currentStreamedPrice
        );
    }

    function cancelStream() external {
        assertIsCreator();

        // Load and update status
        IStreamTypes.Status status = loadStreamStatus();
        IStreamTypes.StreamTimes memory times = loadStreamTimes();
        status = syncStreamStatus(status, times, block.timestamp);

        // Check if operation is allowed
        IStreamTypes.Status[] memory allowedStatuses = new IStreamTypes.Status[](1);
        allowedStatuses[0] = IStreamTypes.Status.Waiting;
        bool isAllowed = isOperationAllowed(status, allowedStatuses);
        if (!isAllowed) {
            revert OperationNotAllowed();
        }

        // Refund out tokens to creator
        safeTokenTransfer(streamTokens.outSupplyToken, creator, streamState.outSupply);

        // Update status
        status = IStreamTypes.Status.Cancelled;
        saveStreamStatus(status);

        emit StreamCancelled(address(this), creator, streamState.outSupply, status);
    }

    function cancelWithAdmin() external {
        assertIsProtocolAdmin();

        // Load and update status
        IStreamTypes.Status status = loadStreamStatus();
        IStreamTypes.StreamTimes memory times = loadStreamTimes();
        status = syncStreamStatus(status, times, block.timestamp);

        // Check if operation is allowed
        IStreamTypes.Status[] memory allowedStatuses = new IStreamTypes.Status[](3);
        allowedStatuses[0] = IStreamTypes.Status.Waiting;
        allowedStatuses[1] = IStreamTypes.Status.Bootstrapping;
        allowedStatuses[2] = IStreamTypes.Status.Active;
        bool isAllowed = isOperationAllowed(status, allowedStatuses);
        if (!isAllowed) {
            revert OperationNotAllowed();
        }

        // Refund out tokens to creator
        safeTokenTransfer(streamTokens.outSupplyToken, creator, streamState.outSupply);

        // Update status
        status = IStreamTypes.Status.Cancelled;
        saveStreamStatus(status);

        emit StreamCancelled(address(this), creator, streamState.outSupply, status);
    }

    /**
     * @dev Checks if an address is a valid ERC20 token
     * @param tokenAddress The token address to validate
     * @param testAccount The account to use for testing the token interface
     * @return isValid True if the address implements the ERC20 interface
     */
    function isValidERC20(address tokenAddress, address testAccount) internal view returns (bool isValid) {
        if (tokenAddress == address(0)) {
            return false;
        }

        try IERC20(tokenAddress).balanceOf(testAccount) returns (uint256) {
            return true;
        } catch {
            return false;
        }
    }

    /**
     * @dev Checks if an account has sufficient token balance
     * @param tokenAddress The ERC20 token address
     * @param account The account to check balance for
     * @param requiredAmount The minimum required balance
     * @return hasEnoughBalance True if the account has sufficient balance
     */
    function hasEnoughBalance(
        address tokenAddress,
        address account,
        uint256 requiredAmount
    ) internal view returns (bool) {
        IERC20 token = IERC20(tokenAddress);
        uint256 balance = token.balanceOf(account);
        return balance >= requiredAmount;
    }

    // Load helpers
    function loadStream() internal view returns (IStreamTypes.StreamState memory) {
        return streamState;
    }

    function loadStreamStatus() internal view returns (IStreamTypes.Status) {
        return streamStatus;
    }

    function loadPosition(address user) internal view returns (PositionTypes.Position memory) {
        return positionStorage.getPosition(user);
    }

    function loadStreamTimes() internal view returns (IStreamTypes.StreamTimes memory) {
        return streamTimes;
    }

    // Save helpers
    function saveStream(IStreamTypes.StreamState memory state) internal {
        streamState = state;
    }

    function saveStreamStatus(IStreamTypes.Status status) internal {
        streamStatus = status;
    }

    function savePosition(address user, PositionTypes.Position memory position) internal {
        positionStorage.updatePosition(user, position);
    }

    // Refactored syncStream to work directly with a provided memory object
    function syncStream(IStreamTypes.StreamState memory state) internal view returns (IStreamTypes.StreamState memory) {
        IStreamTypes.StreamTimes memory times = loadStreamTimes();

        Decimal memory diff = StreamMathLib.calculateDiff(
            block.timestamp,
            times.streamStartTime,
            times.streamEndTime,
            state.lastUpdated
        );

        if (diff.value > 0) {
            state = StreamMathLib.calculateUpdatedState(state, diff);
            state.lastUpdated = block.timestamp;
        }

        return state;
    }

    // Refactored syncStreamStatus to work directly with a provided memory object
    function syncStreamStatus(
        IStreamTypes.Status status,
        IStreamTypes.StreamTimes memory times,
        uint256 nowTime
    ) internal pure returns (IStreamTypes.Status) {
        status = StreamMathLib.calculateStreamStatus(
            status,
            nowTime,
            times.bootstrappingStartTime,
            times.streamStartTime,
            times.streamEndTime
        );

        return status;
    }

    /**
     * @dev Ensure value is non-zero
     * @param value The value to check
     * @param errorMessage The error message to revert with
     */
    function assertNonZero(uint256 value, string memory errorMessage) internal pure {
        if (value == 0) revert(errorMessage);
    }

    /**
     * @dev Ensure sender is the creator
     */
    function assertIsCreator() internal view {
        if (msg.sender != creator) revert Unauthorized();
    }

    /**
     * @dev Ensure sender is the protocol admin
     */
    function assertIsProtocolAdmin() internal view {
        StreamFactory factoryContract = StreamFactory(factory);
        address protocolAdmin = factoryContract.getParams().protocolAdmin;
        if (msg.sender != protocolAdmin) revert Unauthorized();
    }

    /**
     * @dev Ensure status matches expected value
     * @param status Current status to check
     * @param expectedStatus Status that is expected
     */
    function assertStatus(IStreamTypes.Status status, IStreamTypes.Status expectedStatus) internal pure {
        if (status != expectedStatus) revert OperationNotAllowed();
    }

    /**
     * @dev Assert that the cap does not exceed balance
     * @param cap Amount to withdraw
     * @param balance Available balance
     */
    function assertWithinBalance(uint256 cap, uint256 balance) internal pure {
        if (cap > balance) revert WithdrawAmountExceedsBalance(cap);
    }

    /**
     * @dev Get the current stream status
     * @return The current stream status
     */
    function getStreamStatus() external view returns (IStreamTypes.Status) {
        return streamStatus;
    }

    /**
     * @dev Get the current stream state
     * @return The current stream state
     */
    function getStreamState() external view returns (IStreamTypes.StreamState memory) {
        return streamState;
    }
}
","keccak256":"0xfaf9fd3fc8a19731c887a1b3bcb44d930a8251dd15f07c19f4fcb430d9c6d8f5","license":"MIT"},"contracts/StreamErrors.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

interface IStreamErrors {
    // Stream errors
    error InvalidBootstrappingStartTime();
    error InvalidStreamStartTime();
    error InvalidStreamEndTime();
    error StreamDurationTooShort();
    error BootstrappingDurationTooShort();
    error WaitingDurationTooShort();
    error InsufficientTokenPayment(uint256 requiredTokenAmount, uint256 tokenBalance);
    error InvalidOutSupplyToken();
    error InvalidInSupplyToken();
    error PaymentFailed();
    error OperationNotAllowed();
    error Unauthorized();
    error InvalidWithdrawAmount();
    error WithdrawAmountExceedsBalance(uint256 cap);
    error InsufficientOutAmount();
    error InvalidPosition();
    error InvalidExitCondition();
    // StreamFactory errors
    error ContractFrozen();
    error InvalidExitFeeRatio();
    error ZeroOutSupplyNotAllowed();
    error StreamInputTokenNotAccepted();
    error InvalidBootstrappingTime();
    error StreamStartMustBeAfterBootstrapping();
    error StreamEndMustBeAfterStart();
    error InvalidToSVersion();
    error InsufficientNativeToken();
    error FeeTransferFailed();
    error TokenTransferFailed();
    error StreamAddressPredictionFailed();
    error InvalidFeeCollector();
    error InvalidProtocolAdmin();
    error NotAdmin();
}
","keccak256":"0x081aab1a8a41432f4eedde605bf5defa57e100c35076f2d39b7c3c452e01ed74","license":"MIT"},"contracts/StreamEvents.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./StreamTypes.sol";
import "./DecimalMath.sol";

interface IStreamEvents {
    event StreamCreated(
        address indexed streamOutToken,
        address indexed streamInToken,
        address indexed streamFactoryAddress,
        uint256 streamOutAmount,
        uint256 bootstrappingStartTime,
        uint256 streamStartTime,
        uint256 streamEndTime,
        uint256 threshold,
        string streamName,
        string tosVersion,
        address streamAddress,
        uint16 streamId
    );

    event StreamSynced(address indexed streamAddress, IStreamTypes.Status mainStatus, uint256 lastUpdated);

    event Subscribed(
        address indexed streamAddress,
        address indexed subscriber,
        uint256 amountIn,
        uint256 newShares,
        uint256 totalSharesAfter,
        uint256 totalInSupplyAfter
    );

    event StreamSynced(
        address indexed streamAddress,
        uint256 lastUpdated,
        uint8 newStatus,
        Decimal distIndex,
        uint256 outRemaining,
        uint256 inSupply,
        uint256 spentIn,
        Decimal currentStreamedPrice
    );

    event Withdrawn(
        address indexed streamAddress,
        address indexed subscriber,
        uint256 remainingInBalance,
        uint256 remainingShares,
        uint256 totalInSupply,
        uint256 totalShares
    );

    event Exited(
        address indexed streamAddress,
        address indexed subscriber,
        uint256 purchased,
        uint256 spentIn,
        uint256 exitTimestamp
    );

    event StreamFinalized(
        address indexed streamAddress,
        address indexed creator,
        uint256 creatorRevenue,
        uint256 exitFeeAmount,
        IStreamTypes.Status status
    );

    event ParamsUpdated(
        address indexed factory,
        uint256 streamCreationFee,
        uint256 exitFeeRatio,
        uint256 minWaitingDuration,
        uint256 minBootstrappingDuration,
        uint256 minStreamDuration,
        string tosVersion
    );

    event FeeCollectorUpdated(address indexed factory, address newFeeCollector);

    event ProtocolAdminUpdated(address indexed factory, address newProtocolAdmin);

    event FrozenStateUpdated(address indexed factory, bool frozen);

    event AcceptedTokensUpdated(address indexed factory, address[] tokensAdded, address[] tokensRemoved);

    event StreamCancelled(
        address indexed streamAddress,
        address creator,
        uint256 outSupply,
        IStreamTypes.Status status
    );
}
","keccak256":"0x5f68ab5ffb7ec0eb276f4f3e123456229f6000c0f81914a7c289876ca8f6db72","license":"MIT"},"contracts/StreamFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./Stream.sol";
import "./StreamEvents.sol";
import "./StreamErrors.sol";

contract StreamFactory is IStreamEvents, IStreamErrors {
    struct Params {
        uint256 streamCreationFee; // Fixed fee to create a stream
        address streamCreationFeeToken; // Token used for creation fee,
        Decimal exitFeeRatio; // Fee ratio when exiting a stream
        uint256 minWaitingDuration; // Minimum waiting period
        uint256 minBootstrappingDuration; // Minimum bootstrapping period
        uint256 minStreamDuration; // Minimum duration for a stream
        address feeCollector; // Address where fees are collected
        address protocolAdmin; // Admin address for protocol
        string tosVersion; // Terms of service version
    }

    mapping(address => bool) public acceptedInSupplyTokens;

    address public constant NATIVE_TOKEN = address(0);

    uint16 public currentStreamId;

    Params public params;
    mapping(uint16 => address) public streamAddresses;

    bool public frozen;

    constructor(
        uint256 _streamCreationFee,
        address _streamCreationFeeToken,
        Decimal memory _exitFeeRatio,
        uint256 _minWaitingDuration,
        uint256 _minBootstrappingDuration,
        uint256 _minStreamDuration,
        address[] memory _acceptedInSupplyTokens,
        address _feeCollector,
        address _protocolAdmin,
        string memory _tosVersion
    ) {
        if (_feeCollector == address(0)) revert InvalidFeeCollector();
        if (_protocolAdmin == address(0)) revert InvalidProtocolAdmin();

        // Check if exit fee ratio is between 0 and 1
        if (DecimalMath.gt(_exitFeeRatio, DecimalMath.fromNumber(1))) revert InvalidExitFeeRatio();
        params = Params({
            streamCreationFee: _streamCreationFee,
            streamCreationFeeToken: _streamCreationFeeToken,
            exitFeeRatio: _exitFeeRatio,
            minWaitingDuration: _minWaitingDuration,
            minBootstrappingDuration: _minBootstrappingDuration,
            minStreamDuration: _minStreamDuration,
            feeCollector: _feeCollector,
            protocolAdmin: _protocolAdmin,
            tosVersion: _tosVersion
        });

        // Set accepted tokens
        for (uint i = 0; i < _acceptedInSupplyTokens.length; i++) {
            acceptedInSupplyTokens[_acceptedInSupplyTokens[i]] = true;
        }
        currentStreamId = 0;
    }

    modifier onlyAdmin() {
        if (msg.sender != params.protocolAdmin) revert NotAdmin();
        _;
    }

    function updateParams(
        uint256 _streamCreationFee,
        Decimal memory _exitFeeRatio,
        uint256 _minWaitingDuration,
        uint256 _minBootstrappingDuration,
        uint256 _minStreamDuration,
        string memory _tosVersion
    ) external onlyAdmin {
        params.streamCreationFee = _streamCreationFee;
        params.exitFeeRatio = _exitFeeRatio;
        params.minWaitingDuration = _minWaitingDuration;
        params.minBootstrappingDuration = _minBootstrappingDuration;
        params.minStreamDuration = _minStreamDuration;
        params.tosVersion = _tosVersion;

        emit ParamsUpdated(
            address(this),
            _streamCreationFee,
            _exitFeeRatio.value,
            _minWaitingDuration,
            _minBootstrappingDuration,
            _minStreamDuration,
            _tosVersion
        );
    }

    function updateFeeCollector(address _feeCollector) external onlyAdmin {
        if (_feeCollector == address(0)) revert InvalidFeeCollector();
        params.feeCollector = _feeCollector;
        emit FeeCollectorUpdated(address(this), _feeCollector);
    }

    function updateProtocolAdmin(address _protocolAdmin) external onlyAdmin {
        if (_protocolAdmin == address(0)) revert InvalidProtocolAdmin();
        params.protocolAdmin = _protocolAdmin;
        emit ProtocolAdminUpdated(address(this), _protocolAdmin);
    }

    function updateAcceptedTokens(
        address[] calldata tokens_to_add,
        address[] calldata tokens_to_remove
    ) external onlyAdmin {
        for (uint i = 0; i < tokens_to_add.length; i++) {
            acceptedInSupplyTokens[tokens_to_add[i]] = true;
        }
        for (uint i = 0; i < tokens_to_remove.length; i++) {
            acceptedInSupplyTokens[tokens_to_remove[i]] = false;
        }
        emit AcceptedTokensUpdated(address(this), tokens_to_add, tokens_to_remove);
    }

    function createStream(
        uint256 _streamOutAmount,
        address _outSupplyToken,
        uint256 _bootstrappingStartTime,
        uint256 _streamStartTime,
        uint256 _streamEndTime,
        uint256 _threshold,
        string memory _name,
        address _inSupplyToken,
        string memory _tosVersion,
        bytes32 _salt
    ) external payable {
        // Check if contract is accepting new streams (not frozen)
        if (frozen) revert ContractFrozen();

        // Validate input parameters
        if (_streamOutAmount == 0) revert ZeroOutSupplyNotAllowed();
        if (!acceptedInSupplyTokens[_inSupplyToken]) revert StreamInputTokenNotAccepted();

        // Validate time parameters using validateStreamTimes
        validateStreamTimes(block.timestamp, _bootstrappingStartTime, _streamStartTime, _streamEndTime);

        // Validate TOS version
        if (keccak256(abi.encodePacked(_tosVersion)) != keccak256(abi.encodePacked(params.tosVersion)))
            revert InvalidToSVersion();

        // Load creation fee
        uint256 creationFee = params.streamCreationFee;
        if (creationFee > 0) {
            if (params.streamCreationFeeToken == address(0)) {
                // Native token
                if (msg.value < creationFee) revert InsufficientNativeToken();
                // Transfer fee to fee collector
                if (!payable(params.feeCollector).send(creationFee)) revert FeeTransferFailed();
            } else {
                // ERC20 token
                if (
                    !IERC20(params.streamCreationFeeToken).transferFrom(
                        msg.sender,
                        address(params.feeCollector),
                        creationFee
                    )
                ) revert TokenTransferFailed();
            }
        }
        // Predict stream address
        bytes32 bytecodeHash = keccak256(
            abi.encodePacked(
                type(Stream).creationCode,
                abi.encode(
                    _streamOutAmount,
                    _outSupplyToken,
                    _bootstrappingStartTime,
                    _streamStartTime,
                    _streamEndTime,
                    _threshold,
                    _name,
                    _inSupplyToken,
                    msg.sender
                )
            )
        );

        address predictedAddress = predictAddress(address(this), _salt, bytecodeHash);
        // Transfer out denom to stream contract
        if (!IERC20(_outSupplyToken).transferFrom(msg.sender, predictedAddress, _streamOutAmount))
            revert TokenTransferFailed();
        // Deploy new stream contract with all parameters
        Stream newStream = new Stream{ salt: _salt }(
            _streamOutAmount,
            _outSupplyToken,
            _bootstrappingStartTime,
            _streamStartTime,
            _streamEndTime,
            _threshold,
            _name,
            _inSupplyToken,
            msg.sender
        );

        if (address(newStream) != predictedAddress) revert StreamAddressPredictionFailed();
        streamAddresses[currentStreamId] = address(newStream);

        emit StreamCreated(
            _outSupplyToken,
            _inSupplyToken,
            address(this),
            _streamOutAmount,
            _bootstrappingStartTime,
            _streamStartTime,
            _streamEndTime,
            _threshold,
            _name,
            _tosVersion,
            address(newStream),
            currentStreamId
        );
        currentStreamId++;
    }

    function getStreams() external view returns (address[] memory) {
        address[] memory streams = new address[](currentStreamId);
        for (uint16 i = 0; i < currentStreamId; i++) {
            streams[i] = streamAddresses[i];
        }
        return streams;
    }

    function getStream(uint16 _streamId) external view returns (address) {
        return streamAddresses[_streamId];
    }

    function isStream(address _streamAddress) external view returns (bool) {
        for (uint16 i = 0; i < currentStreamId; i++) {
            if (streamAddresses[i] == _streamAddress) {
                return true;
            }
        }
        return false;
    }

    function getParams() external view returns (Params memory) {
        return params;
    }

    function isAcceptedInSupplyToken(address token) public view returns (bool) {
        return acceptedInSupplyTokens[token];
    }

    function getAcceptedInSupplyTokens() external view returns (address[] memory) {
        address[] memory tokens = new address[](currentStreamId);
        for (uint16 i = 0; i < currentStreamId; i++) {
            if (acceptedInSupplyTokens[streamAddresses[i]]) {
                tokens[i] = streamAddresses[i];
            }
        }
        return tokens;
    }

    function setFrozen(bool _frozen) external onlyAdmin {
        frozen = _frozen;
        emit FrozenStateUpdated(address(this), _frozen);
    }

    function predictAddress(address creator, bytes32 _salt, bytes32 bytecodeHash) public pure returns (address) {
        return address(uint160(uint(keccak256(abi.encodePacked(bytes1(0xff), creator, _salt, bytecodeHash)))));
    }

    function validateStreamTimes(
        uint256 nowTime,
        uint256 _bootstrappingStartTime,
        uint256 _startTime,
        uint256 _endTime
    ) internal view {
        if (nowTime > _bootstrappingStartTime) revert InvalidBootstrappingStartTime();
        if (_bootstrappingStartTime > _startTime) revert InvalidStreamStartTime();
        if (_startTime > _endTime) revert InvalidStreamEndTime();
        if (_endTime - _startTime < params.minStreamDuration) revert StreamDurationTooShort();
        if (_startTime - _bootstrappingStartTime < params.minBootstrappingDuration)
            revert BootstrappingDurationTooShort();
        if (_bootstrappingStartTime - nowTime < params.minWaitingDuration) revert WaitingDurationTooShort();
    }
}
