contracts/DecimalMath.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

/**
 * @title DecimalMath
 * @dev Library for handling decimal math operations with fixed-point arithmetic
 */
library DecimalMath {
    // We'll use 6 decimal places for precision
    uint256 public constant DECIMAL_PRECISION = 1e6;

    // Convert a regular number to a decimal representation
    function fromNumber(uint256 value) internal pure returns (uint256) {
        return value * DECIMAL_PRECISION;
    }
    function floor(uint256 value) internal pure returns (uint256) {
        return value / DECIMAL_PRECISION;
    }
    function ceil(uint256 value) internal pure returns (uint256) {
        return (value + DECIMAL_PRECISION - 1) / DECIMAL_PRECISION;
    }

    // Multiply two decimal numbers
    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        return (a * b) / DECIMAL_PRECISION;
    }

    // Divide two decimal numbers
    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return (a * DECIMAL_PRECISION) / b;
    }

    // Add two decimal numbers
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        return a + b;
    }

    // Subtract two decimal numbers
    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return a - b;
    }
}

contracts/PositionStorage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./PositionTypes.sol";   

contract PositionStorage {
    using PositionTypes for PositionTypes.Position;

    mapping(address => PositionTypes.Position) private positions;
    address public immutable streamContractAddress;

    constructor() {
        streamContractAddress = msg.sender;
    }

    function getPosition(address _owner) external view returns (PositionTypes.Position memory) {
        return positions[_owner];
    }

    modifier onlySender() {
        require(msg.sender == streamContractAddress, "Position can only be set by the stream contract");
        _;
    }

    function createPosition(
        address owner,
        uint256 inBalance,
        uint256 shares,
        uint256 index
    ) external onlySender {
        positions[owner] = PositionTypes.Position(inBalance, shares, index, block.timestamp, 0, 0, 0, 0);
    }

    function updatePosition(
        address owner,
        PositionTypes.Position memory position
    ) external onlySender {
        positions[owner] = position;
    }

    function setExitDate(address owner, uint256 exitDate) external onlySender {
        positions[owner].exitDate = exitDate;
    }
}

